#define SystemSetting_Info_Byte1_Calculation    (_COMMUNICATION_USE_PHOTO_ISOALTOR_ * CommUsePhotoIso_Bit)+\
    (_ONE_WIRE_UART_BUTTON_TEMPORARY_SET_ENABLE_ * _1WireBySw_Bit)+\
    (_SW_Button_LEVEL_Reverse_ * SwLevelReverse_Bit)+\
    (_Use_Usart_Port_Mapping_ * UartPortMapping_Bit)+_LED_DISPLAY_NUMBERS_


#define SystemSetting_Info_Byte2_Calculation    (_USE_TOUCH_KEY_WITH_LED_ * UseTouchKey_Cap_Bit)+\
    (_USE_USB_POWER_OUTPUT_MODULE_ * USB_PowerOutputCtr_Bit)+\
    (_AUTO_ENTER_SLEEPING_MODE_PREASS_TO_WAKE_UP_ * AutoSleeping_Bit)+\
    (_ENABLE_NTC_2_DETECTION_ * Use_NTC2_Bit)+\
    (_LED_Control_Reverse_With_Lo_LEVEL_Turn_ON_ * LedLowDriving_Bit)+\
    (_LED_BackLight_Control_ * SW2_BackLight_Bit)+\
    (_Capacity_Showing_Use_Voltage_ * CapShowByVoltage_Bit)+\
    (_Capacity_Showing_Use_Coulomb_Counter_ * CapShowByCoulombCounter_Bit)


#define SystemSetting_Info_Byte3_Calculation    (_Enable_Coulomb_Counter_FCC_RM_ * EnableCoulombCounterFCCRM_Bit)+\
    (_Dynamic_UVP_With_Temperature_ * DUVP_Temp_Bit) + \
    (_AUTO_RTC_PERIOD_WAKE_UP_FOR_AUTO_SLEEPING_ * RtcAutoWakeUp_Bit ) + \
    (_Enable_SW_Button_Only_For_Turn_ON_OFF_System_ * SW_Turn_ON_OFF_DSG_Bit ) + \
    (_Enable_MagneticReedSW_AsButton_Only_For_RxTx_ * MagneticReedSW_For_RxTx_Bit )


